import React, {useState} from 'react';
import Modal from 'react-modal'

function PicItem(props) {
  const [modalIsOn, setModalIsOn] = useState(false)
  const bg = {
    content: {
      position: 'absolute',
      top: '120px',
      left: '100px',
      right: '40px',
      bottom: '40px',
      border: '1px solid #ccc',
      background: 'rgba(0,0,0,0)',
      overflow: 'auto',
      WebkitOverflowScrolling: 'touch',
      borderRadius: '4px',
      outline: 'none',
      padding: '20px'
    }
  };
  const styles = StyleSheet.create({
    containerStyle: {
      flex: 1,
      justifyContent: 'center',
      alignItems:"center"
    },
    modalContainer: {
      width: '75%',
      borderRadius: 10,
      borderWidth: 1,
      borderColor: '#cdd2c9',
      backgroundColor: '#cdd2c9',
  
    }
  })

  return (
    <>
      <li className='pics__item' onClick = {() => setModalIsOn(true)}>
        <div className='pics__item__link'>
          <figure className='pics__item__pic-wrap'>
            <img
              className='pics__item__img'
              alt='my arts'
              src={props.src}
            />
          </figure>
        </div>
      </li>
      <Modal isOpen={modalIsOn} style={styles.containerStyle} backdrop="static" onRequestClose={() => setModalIsOn(false)} centered>
        <img className="modal_img" src={props.src}/>
      </Modal>
    </>
  );
}

export default PicItem;
