import React, {useState} from 'react';
import Modal from 'react-modal'

function PicItem(props) {
  const [modalIsOn, setModalIsOn] = useState(false)
  const bg = {
    content: {
      position: 'absolute',
      top: '40px',
      left: '100px',
      right: '40px',
      bottom: '40px',
      border: '1px solid #ccc',
      background: '#000',
      overflow: 'auto',
      WebkitOverflowScrolling: 'touch',
      justifyContent: 'center',
      borderRadius: '4px',
      outline: 'none',
      padding: '20px'
    }
  };

  return (
    <>
      <li className='pics__item' onClick = {() => setModalIsOn(true)}>
        <div className='pics__item__link'>
          <figure className='pics__item__pic-wrap'>
            <img
              className='pics__item__img'
              alt='my arts'
              src={props.src}
            />
          </figure>
        </div>
      </li>
      <Modal isOpen={modalIsOn} center style={bg}>
        <img className="modal_img" src={props.src}/>
      </Modal>
    </>
  );
}

export default PicItem;
